function loadDoc() {
    var xhttp = new XMLHttpRequest();
    xhttp.onreadystatechange = function() {
        if (this.readyState == 4 && this.status == 200) {
            myFunction(this);
        }
    };
    var filename = document.getElementById("iduser").textContent.trim();
    xhttp.open("GET", "../xmlDocs/contacts-" + filename + ".xml", true);
    xhttp.send();
};

